import React, { useState } from "react";
import "../App.css";

const ComplaintForm = ({ onComplaintSubmitted }) => {
  const [formData, setFormData] = useState({
    submissionType: "public",
    category: "",
    title: "",
    description: "",
    urgency: "low",
    contactInfo: "",
  });
  const [files, setFiles] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const categories = [
    "Academic Issues",
    "Infrastructure",
    "Administrative",
    "Hostel/Accommodation",
    "Library Services",
    "IT Services",
    "Canteen/Food Services",
    "Transportation",
    "Health Services",
    "Others",
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(selectedFiles);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    const submitFormData = new FormData();

    // Append form fields
    Object.keys(formData).forEach((key) => {
      submitFormData.append(key, formData[key]);
    });

    // Append files
    files.forEach((file) => {
      submitFormData.append("files", file);
    });

    try {
      const response = await fetch("http://localhost:5000/submit-complaint", {
        method: "POST",
        body: submitFormData,
      });

      const data = await response.json();

      if (data.success) {
        setShowPopup(true);
        // Reset form
        setFormData({
          submissionType: "public",
          category: "",
          title: "",
          description: "",
          urgency: "low",
          contactInfo: "",
        });
        setFiles([]);
        // Clear file input
        const fileInput = document.querySelector('input[type="file"]');
        if (fileInput) fileInput.value = "";

        // Call callback to refresh dashboard stats
        if (onComplaintSubmitted) {
          onComplaintSubmitted();
        }
      } else {
        setError(data.message || "Failed to submit complaint");
      }
    } catch (err) {
      console.error("Error submitting complaint:", err);
      setError("Network error. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  return (
    <div className="complaint-form-container">
      <div className="form-header">
        <h2>Submit a Complaint</h2>
        <p>Let us know about any issues or feedback you have</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit} className="complaint-form">
        {/* Submission Type */}
        <div className="form-group">
          <label className="form-label">Submission Type *</label>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="public"
                name="submissionType"
                value="public"
                checked={formData.submissionType === "public"}
                onChange={handleInputChange}
              />
              <label htmlFor="public" className="radio-text">
                Public (Trackable)
              </label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="anonymous"
                name="submissionType"
                value="anonymous"
                checked={formData.submissionType === "anonymous"}
                onChange={handleInputChange}
              />
              <label htmlFor="anonymous" className="radio-text">
                Anonymous
              </label>
            </div>
          </div>
        </div>

        {/* Category */}
        <div className="form-group">
          <label htmlFor="category" className="form-label">
            Category *
          </label>
          <select
            id="category"
            name="category"
            value={formData.category}
            onChange={handleInputChange}
            required
            className="form-select"
          >
            <option value="">Select a category</option>
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        {/* Title */}
        <div className="form-group">
          <label htmlFor="title" className="form-label">
            Title *
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            placeholder="Brief title for your complaint"
            required
            className="form-input"
          />
        </div>

        {/* Description */}
        <div className="form-group">
          <label htmlFor="description" className="form-label">
            Description *
          </label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Provide detailed information about your complaint"
            required
            className="form-textarea"
          />
        </div>

        {/* Urgency */}
        <div className="form-group">
          <label htmlFor="urgency" className="form-label">
            Urgency Level *
          </label>
          <select
            id="urgency"
            name="urgency"
            value={formData.urgency}
            onChange={handleInputChange}
            className="form-select"
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
            <option value="critical">Critical</option>
          </select>
        </div>

        {/* Contact Info for Anonymous */}
        {formData.submissionType === "anonymous" && (
          <div className="form-group">
            <label htmlFor="contactInfo" className="form-label">
              Contact Information (Optional)
            </label>
            <input
              type="text"
              id="contactInfo"
              name="contactInfo"
              value={formData.contactInfo}
              onChange={handleInputChange}
              placeholder="Email or phone number (optional)"
              className="form-input"
            />
          </div>
        )}

        {/* File Upload */}
        <div className="form-group">
          <label htmlFor="files" className="form-label">
            Attachments (Optional)
          </label>
          <input
            type="file"
            id="files"
            multiple
            onChange={handleFileChange}
            accept=".jpg,.jpeg,.png,.pdf,.doc,.docx"
            className="form-file-input"
          />
          <div className="file-upload-info">
            <small>
              Supported: JPG, PNG, PDF, DOC, DOCX (Max 5MB each, up to 5 files)
            </small>
          </div>
          {files.length > 0 && (
            <div className="selected-files">
              <h4>Selected Files:</h4>
              <ul>
                {files.map((file, index) => (
                  <li key={index}>
                    {file.name} ({(file.size / 1024 / 1024).toFixed(2)} MB)
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* Submit Button */}
        <button type="submit" disabled={loading} className="submit-btn">
          {loading ? "Submitting..." : "Submit Complaint"}
        </button>
      </form>

      {/* Success Popup */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup-box success-popup">
            <div className="popup-icon">âœ…</div>
            <p>Complaint submitted successfully!</p>
            <p>You will receive updates on the status of your complaint.</p>
            <button onClick={handlePopupClose} className="popup-btn">
              OK
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ComplaintForm;
