import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import ComplaintForm from "./components/ComplaintForm";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(false);
  const [trackingId, setTrackingId] = useState("");
  const [trackingResult, setTrackingResult] = useState(null);
  const [complaintTimeline, setComplaintTimeline] = useState([]);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    resolved: 0,
    inProgress: 0,
  });

  // Fetch complaints and stats
  const fetchComplaints = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/complaints");
      const data = await res.json();

      if (data.success) {
        setComplaints(data.complaints);

        // Calculate stats
        const total = data.complaints.length;
        const pending = data.complaints.filter(
          (c) => c.status === "New"
        ).length;
        const inProgress = data.complaints.filter(
          (c) => c.status === "Under Review"
        ).length;
        const resolved = data.complaints.filter(
          (c) => c.status === "Resolved"
        ).length;

        setStats({ total, pending, resolved, inProgress });
      }
    } catch (error) {
      console.error("Error fetching complaints:", error);
    }
    setLoading(false);
  };

  // Fetch complaint timeline (NEW for Milestone 2)
  const fetchComplaintTimeline = async (complaintId) => {
    try {
      const res = await fetch(
        `http://localhost:5000/complaints/${complaintId}/timeline`
      );
      const data = await res.json();

      if (data.success) {
        setComplaintTimeline(data.timeline);
        return data.timeline;
      }
      return [];
    } catch (error) {
      console.error("Error fetching timeline:", error);
      return [];
    }
  };

  // Track specific complaint with timeline
  const handleTrackComplaint = async () => {
    if (!trackingId.trim()) {
      alert("Please enter a complaint ID");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/complaints/${trackingId}`);
      const data = await res.json();

      if (data.success) {
        setTrackingResult(data.complaint);
        // Fetch timeline for this complaint
        await fetchComplaintTimeline(trackingId);
      } else {
        setTrackingResult(null);
        setComplaintTimeline([]);
        alert("Complaint not found");
      }
    } catch (error) {
      console.error("Error tracking complaint:", error);
      alert("Error tracking complaint");
    }
    setLoading(false);
  };

  // Get status color
  const getStatusColor = (status) => {
    switch (status) {
      case "New":
        return "#ff6b6b";
      case "Under Review":
        return "#feca57";
      case "Resolved":
        return "#48dbfb";
      default:
        return "#ddd";
    }
  };

  // Get status priority
  const getPriorityColor = (urgency) => {
    switch (urgency) {
      case "critical":
        return "#ff3838";
      case "high":
        return "#ff6b6b";
      case "medium":
        return "#feca57";
      case "low":
        return "#48dbfb";
      default:
        return "#ddd";
    }
  };

  // Format date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("en-IN", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  // NEW: Get timeline status for styling
  const getTimelineItemClass = (timelineItem, currentStatus) => {
    const statusOrder = ["New", "Under Review", "Resolved"];
    const itemStatusIndex = statusOrder.indexOf(
      timelineItem.new_status || timelineItem.status
    );
    const currentStatusIndex = statusOrder.indexOf(currentStatus);

    return itemStatusIndex <= currentStatusIndex
      ? "timeline-item active"
      : "timeline-item";
  };

  // NEW: Enhanced Timeline Component for Milestone 2
  const TimelineComponent = ({ timeline, currentStatus }) => {
    if (!timeline || timeline.length === 0) {
      return (
        <div className="status-timeline">
          <h5>Status Timeline</h5>
          <p>No timeline available</p>
        </div>
      );
    }

    return (
      <div className="status-timeline">
        <h5>Status Timeline</h5>
        <div className="timeline">
          {timeline.map((item, index) => (
            <div
              key={item.id || index}
              className={getTimelineItemClass(item, currentStatus)}
            >
              <div className="timeline-dot"></div>
              <div className="timeline-content">
                <h6>{item.new_status || item.status}</h6>
                <p>{item.update_message}</p>
                <small>{formatDate(item.created_at)}</small>
                {item.old_status && (
                  <small
                    style={{
                      display: "block",
                      color: "#999",
                      fontSize: "11px",
                    }}
                  >
                    Changed from: {item.old_status}
                  </small>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  useEffect(() => {
    if (activeTab === "history" || activeTab === "track") {
      fetchComplaints();
    }
  }, [activeTab]);

  const renderTabContent = () => {
    switch (activeTab) {
      case "submit":
        return <ComplaintForm onComplaintSubmitted={fetchComplaints} />;

      case "track":
        return (
          <div className="tab-content">
            <div className="track-complaint-section">
              <h3>Track Your Complaint</h3>
              <div className="track-form">
                <div className="input-group">
                  <input
                    type="text"
                    placeholder="Enter Complaint ID (e.g., COMP1234567890)"
                    value={trackingId}
                    onChange={(e) => setTrackingId(e.target.value)}
                    className="track-input"
                  />
                  <button
                    onClick={handleTrackComplaint}
                    disabled={loading}
                    className="track-btn"
                  >
                    {loading ? "Tracking..." : "Track Status"}
                  </button>
                </div>
              </div>

              {trackingResult && (
                <div className="tracking-result">
                  <div className="complaint-card">
                    <div className="complaint-header">
                      <h4>{trackingResult.title}</h4>
                      <div className="complaint-meta">
                        <span
                          className="status-badge"
                          style={{
                            backgroundColor: getStatusColor(
                              trackingResult.status
                            ),
                          }}
                        >
                          {trackingResult.status}
                        </span>
                        <span
                          className="priority-badge"
                          style={{
                            backgroundColor: getPriorityColor(
                              trackingResult.urgency
                            ),
                          }}
                        >
                          {trackingResult.urgency.charAt(0).toUpperCase() +
                            trackingResult.urgency.slice(1)}{" "}
                          Priority
                        </span>
                      </div>
                    </div>

                    <div className="complaint-details">
                      <p>
                        <strong>Complaint ID:</strong>{" "}
                        {trackingResult.complaint_id}
                      </p>
                      <p>
                        <strong>Category:</strong> {trackingResult.category}
                      </p>
                      <p>
                        <strong>Description:</strong>{" "}
                        {trackingResult.description}
                      </p>
                      <p>
                        <strong>Submitted:</strong>{" "}
                        {formatDate(trackingResult.created_at)}
                      </p>
                      {trackingResult.updated_at &&
                        trackingResult.updated_at !==
                          trackingResult.created_at && (
                          <p>
                            <strong>Last Updated:</strong>{" "}
                            {formatDate(trackingResult.updated_at)}
                          </p>
                        )}
                    </div>

                    {/* Enhanced Timeline Component for Milestone 2 */}
                    <TimelineComponent
                      timeline={complaintTimeline}
                      currentStatus={trackingResult.status}
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        );

      case "history":
        return (
          <div className="tab-content">
            <h3>All Complaints History</h3>
            {loading ? (
              <div className="loading">Loading complaints...</div>
            ) : (
              <div className="complaints-list">
                {complaints.length === 0 ? (
                  <div className="no-complaints">
                    <p>No complaints found. Submit your first complaint!</p>
                  </div>
                ) : (
                  complaints.map((complaint) => (
                    <div key={complaint.id} className="complaint-card">
                      <div className="complaint-header">
                        <h4>{complaint.title}</h4>
                        <div className="complaint-meta">
                          <span
                            className="status-badge"
                            style={{
                              backgroundColor: getStatusColor(complaint.status),
                            }}
                          >
                            {complaint.status}
                          </span>
                          <span
                            className="priority-badge"
                            style={{
                              backgroundColor: getPriorityColor(
                                complaint.urgency
                              ),
                            }}
                          >
                            {complaint.urgency.charAt(0).toUpperCase() +
                              complaint.urgency.slice(1)}
                          </span>
                        </div>
                      </div>

                      <div className="complaint-details">
                        <p>
                          <strong>ID:</strong> {complaint.complaint_id}
                        </p>
                        <p>
                          <strong>Category:</strong> {complaint.category}
                        </p>
                        <p>
                          <strong>Description:</strong>{" "}
                          {complaint.description.substring(0, 100)}...
                        </p>
                        <p>
                          <strong>Submitted:</strong>{" "}
                          {formatDate(complaint.created_at)}
                        </p>
                        {complaint.submission_type === "anonymous" ? (
                          <p>
                            <strong>Type:</strong> Anonymous
                          </p>
                        ) : (
                          <p>
                            <strong>Type:</strong> Public
                          </p>
                        )}
                      </div>

                      <div className="complaint-actions">
                        <button
                          className="view-btn"
                          onClick={() => {
                            setTrackingId(complaint.complaint_id);
                            setActiveTab("track");
                            // Auto-track when clicking view details
                            setTimeout(() => {
                              handleTrackComplaint();
                            }, 100);
                          }}
                        >
                          View Timeline
                        </button>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
        );

      default:
        return <ComplaintForm onComplaintSubmitted={fetchComplaints} />;
    }
  };

  return (
    <div className="dashboard-container">
      <Navbar />

      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome to ResolveIT Portal</h1>
          <p>Your complaints and feedback management system</p>
        </div>

        {/* Dashboard Stats */}
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-number">{stats.total}</div>
            <div className="stat-label">Total Complaints</div>
          </div>
          <div className="stat-card pending">
            <div className="stat-number">{stats.pending}</div>
            <div className="stat-label">New</div>
          </div>
          <div className="stat-card progress">
            <div className="stat-number">{stats.inProgress}</div>
            <div className="stat-label">Under Review</div>
          </div>
          <div className="stat-card resolved">
            <div className="stat-number">{stats.resolved}</div>
            <div className="stat-label">Resolved</div>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="tab-navigation">
          <button
            className={`tab-btn ${activeTab === "submit" ? "active" : ""}`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Complaint
          </button>
          <button
            className={`tab-btn ${activeTab === "track" ? "active" : ""}`}
            onClick={() => setActiveTab("track")}
          >
            Track Status
          </button>
          <button
            className={`tab-btn ${activeTab === "history" ? "active" : ""}`}
            onClick={() => setActiveTab("history")}
          >
            All Complaints
          </button>
        </div>

        {/* Tab Content */}
        <div className="dashboard-main">{renderTabContent()}</div>
      </div>
    </div>
  );
};

export default Dashboard;
