        }

        .filter-select {
          padding: 10px 15px;
          border: 2px solid #e2e8f0;
          border-radius: 8px;
          background: white;
          font-size: 14px;
        }

        .filter-select:focus {
          outline: none;
          border-color: #00897b;
        }

        .clear-filters-btn {
          background: #e2e8f0;
          color: #4a5568;
          border: none;
          padding: 10px 20px;
          border-radius: 8px;
          font-weight: 600;
          cursor: pointer;
        }

        .clear-filters-btn:hover {
          background: #cbd5e0;
        }

        .complaints-table-container {
          overflow-x: auto;
        }

        .complaints-table {
          width: 100%;
          border-collapse: collapse;
        }

        .complaints-table th,
        .complaints-table td {
          padding: 15px;
          text-align: left;
          border-bottom: 1px solid #e2e8f0;
        }

        .complaints-table th {
          background: #f7fafc;
          font-weight: 600;
          color: #4a5568;
        }

        .title-cell {
