  const submitData = new FormData();

  // Add form fields
  Object.keys(formData).forEach((key) => {
    submitData.append(key, formData[key]);
  });

  // Add files
  selectedFiles.forEach((file) => {
    submitData.append("files", file);
  });

  const res = await fetch("http://localhost:5000/submit-complaint", {
    method: "POST",
    body: submitData,
  });

  const data = await res.json();

  if (res.ok) {
    setStatus("success");
    setMessage(
      "Complaint submitted successfully! Your complaint ID is: " +
        (data.complaintId || "COMP" + Date.now())
    );
    setShowPopup(true);

    // Reset form
    setFormData({
      submissionType: "public",
      category: "",
      title: "",
      description: "",
      urgency: "low",
      contactInfo: "",
    });
    setSelectedFiles([]);

    const fileInput = document.getElementById("file-upload");
    if (fileInput) fileInput.value = "";

    // âœ… Call the callback to refresh dashboard stats
    if (onComplaintSubmitted) {
      onComplaintSubmitted();
    }
  } else {
    setStatus("error");
    setMessage(data.message || "Failed to submit complaint");
  }
} catch (err) {
  setStatus("error");
  setMessage("Server error. Please try again later.");
}
