import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import ComplaintForm from "./components/ComplaintForm";
import ComplaintTracker from "./components/ComplaintTracker";
import ComplaintHistory from "./components/ComplaintHistory";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");
  const [stats, setStats] = useState({
    total_complaints: 0,
    new_complaints: 0,
    pending_complaints: 0,
    resolved_complaints: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();

    // Listen for complaint tracking events from history
    const handleTrackComplaint = (event) => {
      setActiveTab("track");
      // You can pass the complaint ID to the tracker component
      window.complaintToTrack = event.detail.complaintId;
    };

    window.addEventListener("trackComplaint", handleTrackComplaint);

    return () => {
      window.removeEventListener("trackComplaint", handleTrackComplaint);
    };
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      if (!user) {
        setLoading(false);
        return;
      }

      const response = await fetch(
        `http://localhost:5000/dashboard-stats/${user.id}`
      );
      const data = await response.json();

      if (data.success) {
        setStats(data.stats);
      }
    } catch (err) {
      console.error("Error fetching dashboard stats:", err);
    } finally {
      setLoading(false);
    }
  };

  // Handle successful complaint submission
  const handleComplaintSubmitted = () => {
    fetchDashboardStats(); // Refresh stats
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case "submit":
        return (
          <ComplaintForm onComplaintSubmitted={handleComplaintSubmitted} />
        );
      case "track":
        return <ComplaintTracker />;
      case "history":
        return <ComplaintHistory />;
      default:
        return (
          <ComplaintForm onComplaintSubmitted={handleComplaintSubmitted} />
        );
    }
  };

  return (
    <div className="dashboard-container">
      <Navbar />

      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome to ResolveIT Portal</h1>
          <p>Your complaints and feedback management system</p>
        </div>

        {/* Dashboard Stats */}
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-number">
              {loading ? "..." : stats.total_complaints}
            </div>
            <div className="stat-label">Total Complaints</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {loading ? "..." : stats.new_complaints}
            </div>
            <div className="stat-label">New</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {loading ? "..." : stats.pending_complaints}
            </div>
            <div className="stat-label">Pending</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {loading ? "..." : stats.resolved_complaints}
            </div>
            <div className="stat-label">Resolved</div>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="tab-navigation">
          <button
            className={`tab-btn ${activeTab === "submit" ? "active" : ""}`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Complaint
          </button>
          <button
            className={`tab-btn ${activeTab === "track" ? "active" : ""}`}
            onClick={() => setActiveTab("track")}
          >
            Track Status
          </button>
          <button
            className={`tab-btn ${activeTab === "history" ? "active" : ""}`}
            onClick={() => setActiveTab("history")}
          >
            My Complaints
          </button>
        </div>

        {/* Tab Content */}
        <div className="dashboard-main">{renderTabContent()}</div>
      </div>
    </div>
  );
};

export default Dashboard;
