import React, { useState, useEffect } from "react";

const ReportsExport = () => {
  const [complaints, setComplaints] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const [exportFilters, setExportFilters] = useState({
    status: "all",
    category: "all",
    dateFrom: "",
    dateTo: "",
    format: "csv"
  });
  const [exporting, setExporting] = useState(false);

  useEffect(() => {
    fetchReportsData();
  }, []);

  const fetchReportsData = async () => {
    try {
      const [complaintsResponse, statsResponse] = await Promise.all([
        fetch("http://localhost:5000/admin/complaints"),
        fetch("http://localhost:5000/stats")
      ]);

      const complaintsData = await complaintsResponse.json();
      const statsData = await statsResponse.json();

      if (complaintsResponse.ok) {
        setComplaints(complaintsData.complaints || []);
      }
      if (statsResponse.ok) {
        setStats(statsData.stats || {});
      }
    } catch (error) {
      console.error("Failed to fetch reports data:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    setExporting(true);
    
    try {
      const queryParams = new URLSearchParams();
      Object.entries(exportFilters).forEach(([key, value]) => {
        if (value && value !== "all") {
          queryParams.append(key, value);
        }
      });

      const response = await fetch(
        `http://localhost:5000/export/complaints?${queryParams.toString()}`
      );

      if (response.ok) {
        if (exportFilters.format === 'csv') {
          const blob = await response.blob();
          const url = window.URL.createObjectURL(blob);
          const link = document.createElement('a');
          link.href = url;
          link.download = `complaints-export-${Date.now()}.csv`;
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
          window.URL.revokeObjectURL(url);
        } else {
          const data = await response.json();
          console.log("Export data:", data);
          alert("Export completed! Check console for data.");
        }
      } else {
        alert("Export failed. Please try again.");
      }
    } catch (error) {
      console.error("Export error:", error);
      alert("Export failed. Please try again.");
    } finally {
      setExporting(false);
    }
  };

  const getCategoryStats = () => {
    const categoryCount = {};
    complaints.forEach(complaint => {
      categoryCount[complaint.category] = (categoryCount[complaint.category] || 0) + 1;
    });
    return Object.entries(categoryCount).sort((a, b) => b[1] - a[1]);
  };

  const getPriorityStats = () => {
    const priorityCount = {};
    complaints.forEach(complaint => {
      priorityCount[complaint.urgency] = (priorityCount[complaint.urgency] || 0) + 1;
    });
    return Object.entries(priorityCount);
  };

  const getMonthlyTrends = () => {
    const monthlyData = {};
    complaints.forEach(complaint => {
      const month = new Date(complaint.created_at).toLocaleString('default', { month: 'long', year: 'numeric' });
      monthlyData[month] = (monthlyData[month] || 0) + 1;
    });
    return Object.entries(monthlyData).slice(-6); // Last 6 months
  };

  const getResolutionTime = () => {
    const resolvedComplaints = complaints.filter(c => c.resolved_at);
    if (resolvedComplaints.length === 0) return 0;
    
    const totalHours = resolvedComplaints.reduce((acc, complaint) => {
      const created = new Date(complaint.created_at);
      const resolved = new Date(complaint.resolved_at);
      const hours = (resolved - created) / (1000 * 60 * 60);
      return acc + hours;
    }, 0);
    
    return Math.round(totalHours / resolvedComplaints.length);
  };

  if (loading) {
    return (
      <div className="reports-loading">
        <div className="loading-spinner"></div>
        <p>Loading reports...</p>
      </div>
    );
  }

  return (
    <div className="reports-container">
      <div className="reports-header">
        <h1>Reports & Analytics</h1>
        <p>Comprehensive insights into complaint trends and performance</p>
      </div>

      {/* Key Metrics Overview */}
      <div className="metrics-overview">
        <div className="metric-card total">
          <div className="metric-icon">ðŸ“Š</div>
          <div className="metric-content">
            <div className="metric-number">{stats.total_complaints || 0}</div>
            <div className="metric-label">Total Complaints</div>
          </div>
        </div>
        
        <div className="metric-card resolution">
          <div className="metric-icon">âš¡</div>
          <div className="metric-content">
            <div className="metric-number">{getResolutionTime()}h</div>
            <div className="metric-label">Avg Resolution Time</div>
          </div>
        </div>
        
        <div className="metric-card satisfaction">
          <div className="metric-icon">ðŸ˜Š</div>
          <div className="metric-content">
            <div className="metric-number">
              {stats.resolved_complaints && stats.total_complaints ? 
                Math.round((stats.resolved_complaints / stats.total_complaints) * 100) : 0}%
            </div>
            <div className="metric-label">Resolution Rate</div>
          </div>
        </div>
        
        <div className="metric-card critical">
          <div className="metric-icon">ðŸš¨</div>
          <div className="metric-content">
            <div className="metric-number">{stats.critical_complaints || 0}</div>
            <div className="metric-label">Critical Issues</div>
          </div>
        </div>
      </div>

      {/* Charts and Analytics */}
      <div className="analytics-grid">
        {/* Status Distribution */}
        <div className="analytics-card">
          <h3>Status Distribution</h3>
          <div className="chart-container">
            {["New", "Under Review", "In Progress", "Resolved", "Escalated"].map(status => {
              const count = complaints.filter(c => c.status === status).length;
              const percentage = complaints.length > 0 ? (count / complaints.length * 100).toFixed(1) : 0;
              const colors = {
                "New": "#3182ce",
                "Under Review": "#d69e2e", 
                "In Progress": "#38a169",
                "Resolved": "#00897b",
                "Escalated": "#e53e3e"
              };
              
              return (
                <div key={status} className="chart-item">
                  <div className="chart-info">
                    <span className="chart-label">{status}</span>
                    <span className="chart-value">{count}</span>
                  </div>
                  <div className="chart-bar">
                    <div 
                      className="chart-fill"
                      style={{ 
                        width: `${percentage}%`,
                        backgroundColor: colors[status]
                      }}
                    ></div>
                  </div>
                  <span className="chart-percentage">{percentage}%</span>
                </div>
              );
            })}
          </div>
        </div>

        {/* Category Breakdown */}
        <div className="analytics-card">
          <h3>Top Categories</h3>
          <div className="category-list">
            {getCategoryStats().slice(0, 5).map(([category, count]) => (
              <div key={category} className="category-item">
                <div className="category-name">{category}</div>
                <div className="category-stats">
                  <span className="category-count">{count}</span>
                  <span className="category-percentage">
                    ({((count / complaints.length) * 100).toFixed(1)}%)
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Priority Distribution */}
        <div className="analytics-card">
          <h3>Priority Levels</h3>
          <div className="priority-grid">
            {getPriorityStats().map(([priority, count]) => {
              const colors = {
                critical: "#e53e3e",
                high: "#dd6b20",
                medium: "#d69e2e", 
                low: "#38a169"
              };
              
              return (
                <div key={priority} className="priority-card">
                  <div 
                    className="priority-icon"
                    style={{ backgroundColor: colors[priority] }}
                  >
                    {priority.charAt(0).toUpperCase()}
                  </div>
                  <div className="priority-info">
                    <div className="priority-count">{count}</div>
                    <div className="priority-name">{priority.toUpperCase()}</div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Monthly Trends */}
        <div className="analytics-card">
          <h3>Monthly Trends</h3>
          <div className="trend-chart">
            {getMonthlyTrends().map(([month, count]) => (
              <div key={month} className="trend-item">
                <div className="trend-bar">
                  <div 
                    className="trend-fill"
                    style={{ 
                      height: `${(count / Math.max(...getMonthlyTrends().map(([,c]) => c))) * 100}%`
                    }}
                  ></div>
                </div>
                <div className="trend-label">{month.split(' ')[0]}</div>
                <div className="trend-value">{count}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Export Section */}
      <div className="export-section">
        <div className="export-header">
          <h2>Export Data</h2>
          <p>Download complaint data for external analysis</p>
        </div>

        <div className="export-filters">
          <div className="filter-row">
            <div className="filter-group">
              <label>Status Filter:</label>
              <select 
                value={exportFilters.status}
                onChange={(e) => setExportFilters({...exportFilters, status: e.target.value})}
                className="export-select"
              >
                <option value="all">All Status</option>
                <option value="New">New</option>
                <option value="Under Review">Under Review</option>
                <option value="In Progress">In Progress</option>
                <option value="Resolved">Resolved</option>
                <option value="Escalated">Escalated</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Category Filter:</label>
              <select 
                value={exportFilters.category}
                onChange={(e) => setExportFilters({...exportFilters, category: e.target.value})}
                className="export-select"
              >
                <option value="all">All Categories</option>
                {Array.from(new Set(complaints.map(c => c.category))).map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>Date From:</label>
              <input 
                type="date"
                value={exportFilters.dateFrom}
                onChange={(e) => setExportFilters({...exportFilters, dateFrom: e.target.value})}
                className="export-input"
              />
            </div>

            <div className="filter-group">
              <label>Date To:</label>
              <input 
                type="date"
                value={exportFilters.dateTo}
                onChange={(e) => setExportFilters({...exportFilters, dateTo: e.target.value})}
                className="export-input"
              />
            </div>
          </div>

          <div className="export-actions">
            <div className="format-selector">
              <label>
                <input 
                  type="radio"
                  name="format"
                  value="csv"
                  checked={exportFilters.format === "csv"}
                  onChange={(e) => setExportFilters({...exportFilters, format: e.target.value})}
                />
                CSV Format
              </label>
              <label>
                <input 
                  type="radio"
                  name="format"
                  value="json"
                  checked={exportFilters.format === "json"}
                  onChange={(e) => setExportFilters({...exportFilters, format: e.target.value})}
                />
                JSON Format
              </label>
            </div>

            <button 
              onClick={handleExport}
              disabled={exporting}
              className="export-btn"
            >
              {exporting ? "Exporting..." : "Export Data"}
            </button>
          </div>
        </div>
      </div>

      <style jsx>{`
        .reports-container {
          padding: 40px;
          max-width: 1400px;
          margin: 0 auto;
          background: #f8fafc;
          min-height: 100vh;
        }

        .reports-loading {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 50vh;
        }

        .loading-spinner {
          width: 40px;
          height: 40px;
          border: 4px solid #f3f3f3;
          border-top: 4px solid #00897b;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-bottom: 20px;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .reports-header {
          text-align: center;
          margin-bottom: 40px;
        }

        .reports-header h1 {
          color: #2d3748;
          margin-bottom: 10px;
        }

        .reports-header p {
          color: #718096;
          margin: 0;
        }

        .metrics-overview {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 25px;
          margin-bottom: 40px;
        }

        .metric-card {
          background: white;
          padding: 30px;
          border-radius: 15px;
          box-shadow: 0 4px 15px rgba(0,0,0,0.05);
          display: flex;
          align-items: center;
          gap: 20px;
          border-left: 5px solid #00897b;
        }

        .metric-card.resolution { border-left-color: #3182ce; }
        .metric-card.satisfaction { border-left-color: #38a169; }
        .metric-card.critical { border-left-color: #e53e3e; }

        .metric-icon {
          font-size: 48px;
          opacity: 0.8;
        }

        .metric-content {
          flex: 1;
        }

        .metric-number {
          font-size: 36px;
          font-weight: bold;
          color: #2d3748;
          margin-bottom: 5px;
        }

        .metric-label {
          color: #718096;
          font-weight: 500;
          font-size: 16px;
        }

        .analytics-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
          gap: 30px;
          margin-bottom: 50px;
        }

        .analytics-card {
          background: white;
          padding: 30px;
          border-radius: 15px;
          box-shadow: 0 4px 15px rgba(0,0,0,0.05);
        }

        .analytics-card h3 {
          color: #2d3748;
          margin-bottom: 25px;
          text-align: center;
          font-size: 20px;
        }

        .chart-container {
          display: flex;
          flex-direction: column;
          gap: 15px;
        }

        .chart-item {
          display: grid;
          grid-template-columns: 140px 1fr 60px;
          gap: 15px;
          align-items: center;
        }

        .chart-info {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .chart-label {
          font-weight: 500;
          color: #4a5568;
          font-size: 14px;
        }

        .chart-value {
          font-weight: 600;
          color: #2d3748;
        }

        .chart-bar {
          background: #e2e8f0;
          height: 12px;
          border-radius: 6px;
          overflow: hidden;
        }

        .chart-fill {
          height: 100%;
          border-radius: 6px;
          transition: width 0.3s ease;
        }

        .chart-percentage {
          text-align: right;
          font-size: 12px;
          color: #718096;
        }

        .category-list {
          display: flex;
          flex-direction: column;
          gap: 15px;
        }

        .category-item {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 15px;
          background: #f7fafc;
          border-radius: 8px;
        }

        .category-name {
          font-weight: 500;
          color: #2d3748;
        }

        .category-stats {
          display: flex;
          gap: 10px;
          align-items: center;
        }

        .category-count {
          font-weight: 600;
          color: #00897b;
        }

        .category-percentage {
          color: #718096;
          font-size: 14px;
        }

        .priority-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
          gap: 15px;
        }

        .priority-card {
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 20px;
          background: #f7fafc;
          border-radius: 12px;
          text-align: center;
        }

        .priority-icon {
          width: 50px;
          height: 50px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-weight: bold;
          font-size: 20px;
          margin-bottom: 10px;
        }

        .priority-count {
          font-size: 24px;
          font-weight: bold;
          color: #2d3748;
        }

        .priority-name {
          color: #718096;
          font-size: 12px;
          font-weight: 500;
        }

        .trend-chart {
          display: flex;
          justify-content: space-between;
          align-items: end;
          height: 200px;
          padding: 20px 0;
        }

        .trend-item {
          display: flex;
          flex-direction: column;
          align-items: center;
          flex: 1;
          max-width: 80px;
        }

        .trend-bar {
          width: 30px;
          height: 140px;
          background: #e2e8f0;
          border-radius: 4px;
          display: flex;
          align-items: end;
          margin-bottom: 10px;
        }

        .trend-fill {
          width: 100%;
          background: #00897b;
          border-radius: 4px;
          transition: height 0.3s ease;
        }

        .trend-label {
          font-size: 12px;
          color: #718096;
          margin-bottom: 5px;
        }

        .trend-value {
          font-weight: 600;
          color: #2d3748;
          font-size: 14px;
        }

        .export-section {
          background: white;
          padding: 40px;
          border-radius: 15px;
          box-shadow: 0 4px 15px rgba(0,0,0,0.05);
        }

        .export-header {
          text-align: center;
          margin-bottom: 30px;
        }

        .export-header h2 {
          color: #2d3748;
          margin-bottom: 10px;
        }

        .export-header p {
          color: #718096;
          margin: 0;
        }

        .export-filters {
          max-width: 800px;
          margin: 0 auto;
        }

        .filter-row {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 20px;
          margin-bottom: 30px;
        }

        .filter-group {
          display: flex;
          flex-direction: column;
          gap: 8px;
        }

        .filter-group label {
          font-weight: 600;
          color: #4a5568;
          font-size: 14px;
        }

        .export-select,
        .export-input {
          padding: 10px 12px;
          border: 2px solid #e2e8f0;
          border-radius: 8px;
          font-size: 14px;
        }

        .export-select:focus,
        .export-input:focus {
          outline: none;
          border-color: #00897b;
        }

        .export-actions {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-top: 20px;
          border-top: 1px solid #e2e8f0;
        }

        .format-selector {
          display: flex;
          gap: 20px;
        }

        .format-selector label {
          display: flex;
          align-items: center;
          gap: 8px;
          cursor: pointer;
          font-weight: 500;
          color: #4a5568;
        }

        .format-selector input[type="radio"] {
          accent-color: #00897b;
        }

        .export-btn {
          background: #00897b;
          color: white;
          border: none;
          padding: 12px 30px;
          border-radius: 8px;
          font-weight: 600;
          cursor: pointer;
          font-size: 16px;
          transition: background-color 0.2s;
        }

        .export-btn:hover:not(:disabled) {
          background: #00796b;
        }

        .export-btn:disabled {
          background: #cbd5e0;
          cursor: not-allowed;
        }

        @media (max-width: 768px) {
          .reports-container {
            padding: 20px;
          }

          .metrics-overview {
            grid-template-columns: 1fr;
          }

          .analytics-grid {
            grid-template-columns: 1fr;
          }

          .filter-row {
            grid-template-columns: 1fr;
          }

          .export-actions {
            flex-direction: column;
            gap: 20px;
            align-items: stretch;
          }

          .format-selector {
            justify-content: center;
          }

          .chart-item {
            grid-template-columns: 100px 1fr 50px;
            gap: 10px;
          }

          .priority-grid {
            grid-template-columns: repeat(2, 1fr);
          }

          .trend-chart {
            height: 150px;
          }

          .trend-bar {
            height: 100px;
          }
        }
      `}</style>
    </div>
  );
};

export default ReportsExport;
