import React, { useState, useEffect } from "react";

const ComplaintTracker = () => {
  const [trackingId, setTrackingId] = useState("");
  const [complaint, setComplaint] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [userComplaints, setUserComplaints] = useState([]);

  // Get user complaints on component mount
  useEffect(() => {
    fetchUserComplaints();
  }, []);

  const fetchUserComplaints = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      if (!user) return;

      const response = await fetch(
        `http://localhost:5000/user-complaints/${user.id}`
      );
      const data = await response.json();

      if (data.success) {
        setUserComplaints(data.complaints);
      }
    } catch (err) {
      console.error("Error fetching user complaints:", err);
    }
  };

  const handleTrackComplaint = async (e) => {
    e.preventDefault();
    if (!trackingId.trim()) {
      setError("Please enter a complaint ID");
      return;
    }

    setLoading(true);
    setError("");
    setComplaint(null);

    try {
      const response = await fetch(
        `http://localhost:5000/complaints/${trackingId}`
      );
      const data = await response.json();

      if (data.success) {
        setComplaint(data.complaint);
        // Fetch complaint timeline
        const timelineResponse = await fetch(
          `http://localhost:5000/complaint-timeline/${trackingId}`
        );
        const timelineData = await timelineResponse.json();

        if (timelineData.success) {
          setComplaint((prev) => ({
            ...prev,
            timeline: timelineData.timeline,
          }));
        }
      } else {
        setError(data.message || "Complaint not found");
      }
    } catch (err) {
      setError("Failed to fetch complaint details");
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "New":
        return "#fbbf24";
      case "Under Review":
        return "#60a5fa";
      case "In Progress":
        return "#a78bfa";
      case "Resolved":
        return "#34d399";
      case "Closed":
        return "#6b7280";
      case "Escalated":
        return "#f87171";
      default:
        return "#9ca3af";
    }
  };

  const getUrgencyColor = (urgency) => {
    switch (urgency) {
      case "low":
        return "#10b981";
      case "medium":
        return "#f59e0b";
      case "high":
        return "#ef4444";
      case "critical":
        return "#dc2626";
      default:
        return "#6b7280";
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <div className="complaint-tracker">
      {/* Search Section */}
      <div className="tracker-search">
        <h3>Track Your Complaint</h3>
        <form onSubmit={handleTrackComplaint} className="track-form">
          <div className="search-input-group">
            <input
              type="text"
              value={trackingId}
              onChange={(e) => setTrackingId(e.target.value)}
              placeholder="Enter Complaint ID (e.g., 1, 2, 3...)"
              className="form-input"
            />
            <button type="submit" disabled={loading} className="track-btn">
              {loading ? "Searching..." : "Track"}
            </button>
          </div>
        </form>
        {error && <div className="error-message">{error}</div>}
      </div>

      {/* Your Complaints Quick Access */}
      {userComplaints.length > 0 && (
        <div className="user-complaints-quick">
          <h4>Your Recent Complaints</h4>
          <div className="complaints-grid">
            {userComplaints.slice(0, 6).map((comp) => (
              <div
                key={comp.id}
                className="quick-complaint-card"
                onClick={() => {
                  setTrackingId(comp.id.toString());
                  handleTrackComplaint({ preventDefault: () => {} });
                }}
              >
                <div className="quick-complaint-id">ID: {comp.id}</div>
                <div className="quick-complaint-title">{comp.title}</div>
                <div
                  className="quick-complaint-status"
                  style={{ backgroundColor: getStatusColor(comp.status) }}
                >
                  {comp.status}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Complaint Details */}
      {complaint && (
        <div className="complaint-details">
          <div className="complaint-header">
            <h3>Complaint Details</h3>
            <div className="complaint-meta">
              <span className="complaint-id">ID: {complaint.id}</span>
              <span
                className="status-badge"
                style={{ backgroundColor: getStatusColor(complaint.status) }}
              >
                {complaint.status}
              </span>
              <span
                className="urgency-badge"
                style={{ backgroundColor: getUrgencyColor(complaint.urgency) }}
              >
                {complaint.urgency.toUpperCase()}
              </span>
            </div>
          </div>

          <div className="complaint-info">
            <div className="info-grid">
              <div className="info-item">
                <label>Title:</label>
                <span>{complaint.title}</span>
              </div>
              <div className="info-item">
                <label>Category:</label>
                <span>{complaint.category}</span>
              </div>
              <div className="info-item">
                <label>Submitted:</label>
                <span>{formatDate(complaint.created_at)}</span>
              </div>
              <div className="info-item">
                <label>Type:</label>
                <span>{complaint.submission_type}</span>
              </div>
            </div>

            <div className="info-item description">
              <label>Description:</label>
              <p>{complaint.description}</p>
            </div>

            {complaint.file_path && (
              <div className="info-item">
                <label>Attachments:</label>
                <div className="attachments">
                  {complaint.file_path.split(",").map((file, index) => (
                    <a
                      key={index}
                      href={`http://localhost:5000/${file}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="attachment-link"
                    >
                      ðŸ“Ž View Attachment {index + 1}
                    </a>
                  ))}
                </div>
              </div>
            )}

            {complaint.resolution_notes && (
              <div className="info-item resolution">
                <label>Resolution Notes:</label>
                <p>{complaint.resolution_notes}</p>
              </div>
            )}
          </div>

          {/* Timeline */}
          {complaint.timeline && complaint.timeline.length > 0 && (
            <div className="complaint-timeline">
              <h4>Status Timeline</h4>
              <div className="timeline">
                {complaint.timeline.map((update, index) => (
                  <div key={update.id} className="timeline-item">
                    <div className="timeline-marker">
                      <div
                        className="timeline-dot"
                        style={{
                          backgroundColor: getStatusColor(update.new_status),
                        }}
                      ></div>
                      {index !== complaint.timeline.length - 1 && (
                        <div className="timeline-line"></div>
                      )}
                    </div>
                    <div className="timeline-content">
                      <div className="timeline-header">
                        <span className="timeline-status">
                          {update.new_status}
                        </span>
                        <span className="timeline-date">
                          {formatDate(update.created_at)}
                        </span>
                      </div>
                      {update.update_message && (
                        <p className="timeline-message">
                          {update.update_message}
                        </p>
                      )}
                      {update.old_status && (
                        <small className="timeline-change">
                          Status changed from "{update.old_status}" to "
                          {update.new_status}"
                        </small>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ComplaintTracker;
