import React, { useState, useEffect } from "react";
import "../App.css";

const ComplaintHistory = ({ user }) => {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (user && user.id) {
      fetchUserComplaints();
    } else {
      setError("Please log in to view your complaints");
      setLoading(false);
    }
  }, [user]);

  const fetchUserComplaints = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/user-complaints/${user.id}`
      );
      const data = await response.json();

      if (response.ok && data.success) {
        setComplaints(data.complaints);
      } else {
        setError(data.message || "Failed to fetch complaints");
      }
    } catch (err) {
      setError("Error loading complaints. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const openComplaintModal = async (complaintId) => {
    try {
      const response = await fetch(
        `http://localhost:5000/complaints/${complaintId}`
      );
      const data = await response.json();

      if (response.ok && data.success) {
        setSelectedComplaint(data.complaint);
        setShowModal(true);
      } else {
        alert("Failed to load complaint details");
      }
    } catch (err) {
      alert("Error loading complaint details");
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedComplaint(null);
  };

  const getStatusColor = (status) => {
    const statusColors = {
      New: "#6c757d",
      "Under Review": "#fd7e14",
      "In Progress": "#0d6efd",
      Resolved: "#198754",
      Closed: "#6f42c1",
      Escalated: "#dc3545",
    };
    return statusColors[status] || "#6c757d";
  };

  const getUrgencyColor = (urgency) => {
    const urgencyColors = {
      low: "#28a745",
      medium: "#ffc107",
      high: "#fd7e14",
      critical: "#dc3545",
    };
    return urgencyColors[urgency] || "#6c757d";
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  if (loading) {
    return (
      <div className="history-container">
        <div className="loading-message">
          <p>Loading your complaints...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="history-container">
        <div className="error-message">
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="history-container">
      <div className="history-header">
        <h2>My Complaints</h2>
        <p>View and track all your submitted complaints</p>
      </div>

      {complaints.length === 0 ? (
        <div className="no-complaints">
          <p>You haven't submitted any complaints yet.</p>
          <p>Click on "Submit Complaint" to get started.</p>
        </div>
      ) : (
        <div className="complaints-grid">
          {complaints.map((complaint) => (
            <div key={complaint.id} className="complaint-card">
              <div className="card-header">
                <h3>{complaint.title}</h3>
                <div className="card-badges">
                  <span
                    className="status-badge"
                    style={{
                      backgroundColor: getStatusColor(complaint.status),
                    }}
                  >
                    {complaint.status}
                  </span>
                  <span
                    className="urgency-badge"
                    style={{
                      backgroundColor: getUrgencyColor(complaint.urgency),
                    }}
                  >
                    {complaint.urgency.toUpperCase()}
                  </span>
                </div>
              </div>

              <div className="card-content">
                <p className="category">Category: {complaint.category}</p>
                <p className="description">
                  {complaint.description.length > 100
                    ? complaint.description.substring(0, 100) + "..."
                    : complaint.description}
                </p>
                <div className="card-meta">
                  <p className="submitted-date">
                    Submitted: {formatDate(complaint.created_at)}
                  </p>
                  {complaint.file_names && (
                    <p className="file-count">
                      📎 {complaint.file_names.split(",").length} file(s)
                      attached
                    </p>
                  )}
                </div>
              </div>

              <div className="card-actions">
                <button
                  className="view-details-btn"
                  onClick={() => openComplaintModal(complaint.id)}
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Modal for complaint details */}
      {showModal && selectedComplaint && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{selectedComplaint.title}</h3>
              <button className="close-btn" onClick={closeModal}>
                ×
              </button>
            </div>

            <div className="modal-body">
              <div className="complaint-meta-info">
                <div className="meta-row">
                  <span className="label">Status:</span>
                  <span
                    className="status-badge"
                    style={{
                      backgroundColor: getStatusColor(selectedComplaint.status),
                    }}
                  >
                    {selectedComplaint.status}
                  </span>
                </div>
                <div className="meta-row">
                  <span className="label">Priority:</span>
                  <span
                    className="urgency-badge"
                    style={{
                      backgroundColor: getUrgencyColor(
                        selectedComplaint.urgency
                      ),
                    }}
                  >
                    {selectedComplaint.urgency.toUpperCase()}
                  </span>
                </div>
                <div className="meta-row">
                  <span className="label">Category:</span>
                  <span>{selectedComplaint.category}</span>
                </div>
                <div className="meta-row">
                  <span className="label">Submitted:</span>
                  <span>{formatDate(selectedComplaint.created_at)}</span>
                </div>
                {selectedComplaint.resolved_at && (
                  <div className="meta-row">
                    <span className="label">Resolved:</span>
                    <span>{formatDate(selectedComplaint.resolved_at)}</span>
                  </div>
                )}
              </div>

              <div className="description-section">
                <h4>Description</h4>
                <p>{selectedComplaint.description}</p>
              </div>

              {selectedComplaint.files &&
                selectedComplaint.files.length > 0 && (
                  <div className="files-section">
                    <h4>Attached Files</h4>
                    <div className="file-list">
                      {selectedComplaint.files.map((file, index) => (
                        <div key={index} className="file-item">
                          <span className="file-name">
                            {file.original_filename}
                          </span>
                          <span className="file-size">
                            ({(file.file_size / 1024 / 1024).toFixed(2)} MB)
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

              {selectedComplaint.timeline &&
                selectedComplaint.timeline.length > 0 && (
                  <div className="timeline-section">
                    <h4>Status Timeline</h4>
                    <div className="timeline">
                      {selectedComplaint.timeline.map((update, index) => (
                        <div key={index} className="timeline-item">
                          <div className="timeline-marker"></div>
                          <div className="timeline-content">
                            <div className="timeline-header">
                              <span
                                className="timeline-status"
                                style={{
                                  color: getStatusColor(update.new_status),
                                }}
                              >
                                {update.new_status}
                              </span>
                              <span className="timeline-date">
                                {formatDate(update.created_at)}
                              </span>
                            </div>
                            {update.update_message && (
                              <p className="timeline-message">
                                {update.update_message}
                              </p>
                            )}
                            {update.updated_by_name && (
                              <p className="timeline-updated-by">
                                Updated by: {update.updated_by_name}
                              </p>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ComplaintHistory;
