import React, { useState, useEffect } from "react";

const SimpleAdmin = () => {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(false);
  const [updating, setUpdating] = useState(null);

  // Fetch all complaints
  const fetchComplaints = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/complaints");
      const data = await res.json();
      if (data.success) {
        setComplaints(data.complaints);
      }
    } catch (error) {
      console.error("Error fetching complaints:", error);
    }
    setLoading(false);
  };

  // Update complaint status
  const updateStatus = async (complaintId, newStatus, comment) => {
    setUpdating(complaintId);
    try {
      const res = await fetch(
        `http://localhost:5000/complaints/${complaintId}/status`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            status: newStatus,
            adminComment: comment || `Status updated to ${newStatus}`,
          }),
        }
      );

      const data = await res.json();
      if (data.success) {
        alert(`Complaint ${complaintId} status updated to ${newStatus}`);
        fetchComplaints(); // Refresh list
      } else {
        alert("Failed to update status: " + data.message);
      }
    } catch (error) {
      console.error("Error updating status:", error);
      alert("Error updating status");
    }
    setUpdating(null);
  };

  // Status action buttons
  const StatusActions = ({ complaint }) => {
    const currentStatus = complaint.status;

    const getNextActions = (status) => {
      switch (status) {
        case "New":
          return [
            { status: "Under Review", label: "Start Review", color: "#feca57" },
          ];
        case "Under Review":
          return [
            { status: "Resolved", label: "Mark Resolved", color: "#48dbfb" },
          ];
        case "Resolved":
          return [
            { status: "Under Review", label: "Reopen", color: "#feca57" },
          ];
        default:
          return [];
      }
    };

    const actions = getNextActions(currentStatus);

    return (
      <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
        {actions.map((action) => (
          <button
            key={action.status}
            onClick={() => {
              const comment = prompt(`Add a comment for ${action.label}:`);
              if (comment !== null) {
                updateStatus(complaint.id, action.status, comment);
              }
            }}
            disabled={updating === complaint.id}
            style={{
              backgroundColor: action.color,
              color: "white",
              border: "none",
              padding: "8px 16px",
              borderRadius: "20px",
              cursor: updating === complaint.id ? "not-allowed" : "pointer",
              fontSize: "12px",
              fontWeight: "600",
              opacity: updating === complaint.id ? 0.6 : 1,
            }}
          >
            {updating === complaint.id ? "Updating..." : action.label}
          </button>
        ))}
      </div>
    );
  };

  useEffect(() => {
    fetchComplaints();
  }, []);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("en-IN", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "New":
        return "#ff6b6b";
      case "Under Review":
        return "#feca57";
      case "Resolved":
        return "#48dbfb";
      default:
        return "#ddd";
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "1200px", margin: "0 auto" }}>
      <div style={{ marginBottom: "30px", textAlign: "center" }}>
        <h2 style={{ color: "#2c3e50" }}>
          Admin Dashboard - Complaint Management
        </h2>
        <p style={{ color: "#6c757d" }}>
          Update complaint statuses and add admin comments
        </p>
        <button
          onClick={fetchComplaints}
          disabled={loading}
          style={{
            background: "linear-gradient(45deg, #667eea, #764ba2)",
            color: "white",
            border: "none",
            padding: "12px 24px",
            borderRadius: "25px",
            cursor: "pointer",
            marginTop: "10px",
          }}
        >
          {loading ? "Refreshing..." : "Refresh Complaints"}
        </button>
      </div>

      {loading ? (
        <div style={{ textAlign: "center", padding: "40px" }}>
          <p>Loading complaints...</p>
        </div>
      ) : (
        <div>
          {complaints.length === 0 ? (
            <p style={{ textAlign: "center", color: "#6c757d" }}>
              No complaints found.
            </p>
          ) : (
            complaints.map((complaint) => (
              <div
                key={complaint.id}
                style={{
                  background: "white",
                  borderRadius: "15px",
                  padding: "20px",
                  marginBottom: "20px",
                  boxShadow: "0 5px 15px rgba(0, 0, 0, 0.08)",
                  borderLeft: `5px solid ${getStatusColor(complaint.status)}`,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "flex-start",
                    marginBottom: "15px",
                  }}
                >
                  <div>
                    <h4 style={{ color: "#2c3e50", margin: "0 0 5px 0" }}>
                      {complaint.title}
                    </h4>
                    <p
                      style={{
                        color: "#6c757d",
                        margin: "0",
                        fontSize: "14px",
                      }}
                    >
                      ID: {complaint.complaint_id}
                    </p>
                  </div>
                  <span
                    style={{
                      backgroundColor: getStatusColor(complaint.status),
                      color: "white",
                      padding: "6px 12px",
                      borderRadius: "20px",
                      fontSize: "12px",
                      fontWeight: "600",
                    }}
                  >
                    {complaint.status}
                  </span>
                </div>

                <div style={{ marginBottom: "15px" }}>
                  <p style={{ margin: "5px 0", color: "#495057" }}>
                    <strong>Category:</strong> {complaint.category}
                  </p>
                  <p style={{ margin: "5px 0", color: "#495057" }}>
                    <strong>Priority:</strong> {complaint.urgency}
                  </p>
                  <p style={{ margin: "5px 0", color: "#495057" }}>
                    <strong>Description:</strong>{" "}
                    {complaint.description.substring(0, 150)}
                    {complaint.description.length > 150 ? "..." : ""}
                  </p>
                  <p
                    style={{
                      margin: "5px 0",
                      color: "#495057",
                      fontSize: "13px",
                    }}
                  >
                    <strong>Submitted:</strong>{" "}
                    {formatDate(complaint.created_at)}
                  </p>
                  {complaint.updated_at !== complaint.created_at && (
                    <p
                      style={{
                        margin: "5px 0",
                        color: "#495057",
                        fontSize: "13px",
                      }}
                    >
                      <strong>Last Updated:</strong>{" "}
                      {formatDate(complaint.updated_at)}
                    </p>
                  )}
                  {complaint.admin_comment && (
                    <p
                      style={{
                        margin: "5px 0",
                        color: "#667eea",
                        fontSize: "13px",
                      }}
                    >
                      <strong>Admin Comment:</strong> {complaint.admin_comment}
                    </p>
                  )}
                </div>

                <StatusActions complaint={complaint} />
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default SimpleAdmin;
