import React, { useState } from "react";
import "../App.css";

const ComplaintTracker = ({ user }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [complaint, setComplaint] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setError("Please enter a complaint ID");
      return;
    }

    setLoading(true);
    setError(null);
    setComplaint(null);

    try {
      const response = await fetch(
        `http://localhost:5000/complaints/${searchQuery}`
      );
      const data = await response.json();

      if (response.ok && data.success) {
        setComplaint(data.complaint);
      } else {
        setError(data.message || "Complaint not found");
      }
    } catch (err) {
      setError("Error searching for complaint. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    const statusColors = {
      New: "#6c757d",
      "Under Review": "#fd7e14",
      "In Progress": "#0d6efd",
      Resolved: "#198754",
      Closed: "#6f42c1",
      Escalated: "#dc3545",
    };
    return statusColors[status] || "#6c757d";
  };

  const getUrgencyColor = (urgency) => {
    const urgencyColors = {
      low: "#28a745",
      medium: "#ffc107",
      high: "#fd7e14",
      critical: "#dc3545",
    };
    return urgencyColors[urgency] || "#6c757d";
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <div className="complaint-tracker-container">
      <div className="tracker-header">
        <h2>Track Your Complaint</h2>
        <p>Enter your complaint ID to view status and timeline</p>
      </div>

      <div className="search-section">
        <form onSubmit={handleSearch} className="search-form">
          <div className="search-input-group">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Enter Complaint ID (e.g., 1, 2, 3...)"
              className="search-input"
            />
            <button type="submit" disabled={loading} className="search-btn">
              {loading ? "Searching..." : "Track"}
            </button>
          </div>
        </form>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      {complaint && (
        <div className="complaint-details">
          <div className="complaint-header-info">
            <div className="complaint-meta">
              <h3>{complaint.title}</h3>
              <div className="meta-tags">
                <span
                  className="status-badge"
                  style={{ backgroundColor: getStatusColor(complaint.status) }}
                >
                  {complaint.status}
                </span>
                <span
                  className="urgency-badge"
                  style={{
                    backgroundColor: getUrgencyColor(complaint.urgency),
                  }}
                >
                  {complaint.urgency.toUpperCase()} PRIORITY
                </span>
                <span className="category-badge">{complaint.category}</span>
              </div>
            </div>
            <div className="complaint-dates">
              <p>
                <strong>Submitted:</strong> {formatDate(complaint.created_at)}
              </p>
              {complaint.resolved_at && (
                <p>
                  <strong>Resolved:</strong> {formatDate(complaint.resolved_at)}
                </p>
              )}
            </div>
          </div>

          <div className="complaint-content">
            <div className="description-section">
              <h4>Description</h4>
              <p>{complaint.description}</p>
            </div>

            {complaint.files && complaint.files.length > 0 && (
              <div className="files-section">
                <h4>Attached Files</h4>
                <div className="file-list">
                  {complaint.files.map((file, index) => (
                    <div key={index} className="file-item">
                      <span className="file-name">
                        {file.original_filename}
                      </span>
                      <span className="file-size">
                        ({(file.file_size / 1024 / 1024).toFixed(2)} MB)
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {complaint.timeline && complaint.timeline.length > 0 && (
            <div className="timeline-section">
              <h4>Status Timeline</h4>
              <div className="timeline">
                {complaint.timeline.map((update, index) => (
                  <div key={index} className="timeline-item">
                    <div className="timeline-marker"></div>
                    <div className="timeline-content">
                      <div className="timeline-header">
                        <span
                          className="timeline-status"
                          style={{ color: getStatusColor(update.new_status) }}
                        >
                          {update.new_status}
                        </span>
                        <span className="timeline-date">
                          {formatDate(update.created_at)}
                        </span>
                      </div>
                      {update.update_message && (
                        <p className="timeline-message">
                          {update.update_message}
                        </p>
                      )}
                      {update.updated_by_name && (
                        <p className="timeline-updated-by">
                          Updated by: {update.updated_by_name}
                        </p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {!complaint && !loading && !error && (
        <div className="no-data-message">
          <p>Enter a complaint ID above to track its status</p>
        </div>
      )}
    </div>
  );
};

export default ComplaintTracker;
