import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import ComplaintForm from "./components/ComplaintForm";
import ComplaintTracking from "./components/ComplaintTracking";
import ComplaintHistory from "./components/ComplaintHistory";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    resolved: 0,
    inProgress: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserStats();
  }, []);

  const fetchUserStats = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const userId = user?.id || 0;
      
      // Fetch user complaints for stats
      const response = await fetch(`http://localhost:5000/user-complaints/${userId}`);
      const data = await response.json();
      
      if (response.ok && data.complaints) {
        const complaints = data.complaints;
        const total = complaints.length;
        const pending = complaints.filter(c => 
          ["New", "Under Review"].includes(c.status)
        ).length;
        const inProgress = complaints.filter(c => c.status === "In Progress").length;
        const resolved = complaints.filter(c => 
          ["Resolved", "Closed"].includes(c.status)
        ).length;
        
        setStats({ total, pending, resolved, inProgress });
      }
    } catch (error) {
      console.error("Failed to fetch user stats:", error);
    } finally {
      setLoading(false);
    }
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case "submit":
        return <ComplaintForm onComplaintSubmitted={fetchUserStats} />;
      case "track":
        return <ComplaintTracking />;
      case "history":
        return <ComplaintHistory />;
      default:
        return <ComplaintForm onComplaintSubmitted={fetchUserStats} />;
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <Navbar />
        <div className="dashboard-loading">
          <div className="loading-spinner"></div>
          <p>Loading dashboard...</p>
        </div>

        {/* Tab Navigation */}
        <div className="tab-navigation">
          <button
            className={`tab-btn ${activeTab === "submit" ? "active" : ""}`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Complaint
          </button>
          <button
            className={`tab-btn ${activeTab === "track" ? "active" : ""}`}
            onClick={() => setActiveTab("track")}
          >
            Track Status
          </button>
          <button
            className={`tab-btn ${activeTab === "history" ? "active" : ""}`}
            onClick={() => setActiveTab("history")}
          >
            My Complaints
          </button>
        </div>

        {/* Tab Content */}
        <div className="dashboard-main">{renderTabContent()}</div>
      <style jsx>{`
        .dashboard-loading {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 50vh;
        }

        .loading-spinner {
          width: 40px;
          height: 40px;
          border: 4px solid #f3f3f3;
          border-top: 4px solid #00897b;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-bottom: 20px;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .stat-card.pending {
          border-left: 4px solid #d69e2e;
        }

        .stat-card.progress {
          border-left: 4px solid #38a169;
        }

        .stat-card.resolved {
          border-left: 4px solid #00897b;
        }
      `}</style>
    </div>
  );
};

  return (
    <div className="dashboard-container">
      <Navbar />

      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome to ResolveIT Portal</h1>
          <p>Your complaints and feedback management system</p>
        </div>

        {/* Dashboard Stats */}
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-number">{stats.total}</div>
            <div className="stat-label">Total Complaints</div>
          </div>
          <div className="stat-card pending">
            <div className="stat-number">{stats.pending}</div>
            <div className="stat-label">Pending</div>
          </div>
          <div className="stat-card progress">
            <div className="stat-number">{stats.inProgress}</div>
            <div className="stat-label">In Progress</div>
          </div>
                    <div className="stat-card resolved">
                      <div className="stat-number">{stats.resolved}</div>
                      <div className="stat-label">Resolved</div>
                    </div>
                  </div>
          
                  {/* Tab Navigation */}
                  <div className="tab-navigation">
                    <button
                      className={`tab-btn ${activeTab === "submit" ? "active" : ""}`}
                      onClick={() => setActiveTab("submit")}
                    >
                      Submit Complaint
                    </button>
                    <button
                      className={`tab-btn ${activeTab === "track" ? "active" : ""}`}
                      onClick={() => setActiveTab("track")}
                    >
                      Track Status
                    </button>
                    <button
                      className={`tab-btn ${activeTab === "history" ? "active" : ""}`}
                      onClick={() => setActiveTab("history")}
                    >
                      My Complaints
                    </button>
                  </div>
          
                  {/* Tab Content */}
                  <div className="dashboard-main">{renderTabContent()}</div>
                </div>
          
                <style jsx>{`
                  .dashboard-loading {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    justify-content: center;
                    height: 50vh;
                  }
          
                  .loading-spinner {
                    width: 40px;
                    height: 40px;
                    border: 4px solid #f3f3f3;
                    border-top: 4px solid #00897b;
                    border-radius: 50%;
                    animation: spin 1s linear infinite;
                    margin-bottom: 20px;
                  }
          
                  @keyframes spin {
                    0% { transform: rotate(0deg); }
                    100% { transform: rotate(360deg); }
                  }
          
                  .stat-card.pending {
                    border-left: 4px solid #d69e2e;
                  }
          
                  .stat-card.progress {
                    border-left: 4px solid #38a169;
                  }
          
                  .stat-card.resolved {
                    border-left: 4px solid #00897b;
                  }
                `}</style>
              </div>
            );
          }

export default Dashboard;
